'use strict';
import gsap from 'gsap';
import { TextPlugin } from 'gsap/TextPlugin';
gsap.registerPlugin(TextPlugin);
var MigleardAnimation=class MigleardAnimation {

    constructor(element, newclass, duration, opacity) {

        this.element = element;
        this.newclass = newclass;
        this.duration = duration;
        this.opacity = opacity;
        let tl = gsap.timeline();
    }
    hover() {
        tl.to(element, { opacity: opacity, duration: duration })
        tl.set(element, {
            text: {
                value: "",
                newClass: newclass,
                delimiter: " "
            },
        });
        tl.to(element, { opacity: 1, duration: duration })

    }



}
export default MigleardAnimation;